export const reducer = (state, action) => {
//ACA VA EL SWICH CASE
switch(action.type){
    case 'GET_USERS':
        return {...state, list: action.payload}
    case 'ADD_FAV': 
    const isAlreadyFav = state.favs.some((favItem) => favItem.id === action.payload.id);
    if (!isAlreadyFav) {
        return {...state, favs: [...state.favs, action.payload] }
    } else {
            return state;
        }
    case 'REMOVE_FAV':
        // Filtrar el dentista a eliminar de favoritos
        const updatedFavs = state.favs.filter((favItem) => favItem.id !== action.payload.id);
        return { ...state, favs: updatedFavs };
  
      default:
        return state;
}
}
