import React from 'react';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; //sume el switch
import Footer from "./Components/Footer";
import Navbar from "./Components/Navbar";
import Home from './Routes/Home'
import Favs from './Routes/Favs'
import Detail from './Routes/Detail'
import Contact from './Routes/Contact';
//import ThemeSwitch from '../Components/utils/ThemeSwitch';
import { ContextProvider } from './Components/utils/global.context';
import { useUserStates } from './Components/utils/global.context'; //sume esto

function App() {
  const { state } = useUserStates(); //sume esto

  return (
<ContextProvider>
  <Router>
    <div>
    <Navbar/>

    <Routes>
      <Route path='/' element={<Home theme={state.theme} />} />
      <Route path='/home' element={<Home theme={state.theme} />} />
      <Route path='/favs' element={<Favs theme={state.theme} />} />
      <Route path='/detail/:id' element={<Detail theme={state.theme} />} />
      <Route path='/contacts' element={<Contact theme={state.theme} />} />
  </Routes>

  <Footer/>
  </div>
  </Router>  
  </ContextProvider>  

  );
}

export default App;
