import React from "react";
import { Link } from 'react-router-dom' 
import { useUserStates } from '../Components/utils/global.context';


  const Card = ({ item}) => {
    const { state, dispatch } = useUserStates();

  const addFav = ()=>{
    // Accedo a localStorage y obtengo los favoritos actuales (si existen)
    const isAlreadyFav = state.favs.some((favItem) => favItem.id === item.id);

    if (!isAlreadyFav) {
    dispatch({ type: 'ADD_FAV', payload: item })
    alert('¡Añadido a favoritos!');
  } else {
      alert('¡Este elemento ya está en tus favoritos!');
    }

  } 

  const toggleFav = () => {
    // Accedo a localStorage y obtengo los favoritos actuales (si existen)
    dispatch({ type: 'TOGGLE_FAV', payload: item });
  }

  return (
    <div className="card">
        <Link to={'/detail/' + item.id} /* En cada card deberan mostrar en name - username y el id */> 
        <img src={item.image} alt="public\images\doctor.jpg" />
        <h4>{item.name + item.username + item.id}</h4> 

        </Link>

        <button onClick={toggleFav} className="favButton">⭐</button>
    </div>
  );
};

export default Card;
