import React, { useContext, useEffect } from 'react'
import { Link } from 'react-router-dom'
//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context
//import { ContextGlobal } from './utils/global.context';
import { useUserStates } from '../Components/utils/global.context';
//import ThemeSwitch from '../Components/utils/ThemeSwitch';

const Navbar = () => {
  //const { state, toggleTheme } = useContext(ContextGlobal);
  const { state, dispatch } = useUserStates(); //agregando , dispatch 

  useEffect(() => {
  const htmlElement = document.documentElement;

  htmlElement.setAttribute(
    'data-bs-theme',
    state.theme === 'dark' ? 'dark' : 'light'
  );
}, [state.theme]);

  const handleThemeChange = () => {
    //toggleTheme();    
    dispatch({ type: 'TOGGLE_THEME' });//agregando esto dispatch({ type: 'TOGGLE_THEME' });
  };

  return (
    <nav>
      {<Link to='/home'><h4>Home</h4></Link>/* Aqui deberan agregar los liks correspondientes a las rutas definidas */}
      {<Link to='/contacts'><h4>Contact</h4></Link>/* Deberan implementar ademas la logica para cambiar de Theme con el button */}
      {<Link to='/favs'><h4>Favs</h4></Link>}

      <button onClick={handleThemeChange}>Change theme</button>

    </nav>
    );
}

export default Navbar;
