import React from "react";
import { Link } from 'react-router-dom' 
import { useUserStates } from '../utils/global.context'; 

//const Card = ({ name, username, id }) => {
  const Card = ({ item}) => {
    const { dispatch } = useUserStates();

  const addFav = ()=>{
    // Accedo a localStorage y obtengo los favoritos actuales (si existen)
    dispatch({ type: 'ADD_FAV', payload: item });
    /*const favsFromStorage = JSON.parse(localStorage.getItem('favs')) || [];

    // Verifico si el item ya está en favoritos
