import Card from '../Components/Card'
//import {ContextProvider} from '../Components/utils/global.context'
import { ContextGlobal, useUserStates } from '../Components/utils/global.context'
//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Home = () => {

 //const {state} = ContextProvider()
 const { state } = useUserStates()//useContext(ContextGlobal);

  return (
    <main className="" >
      <h1>Home</h1>
      <div className='card-grid'>  

      {state && state.data.map((item) => (//EN LA LINEA DE ARRIBA SE DEBEN RENDERIZAR LAS CARDS
        <Card item={item} key={item.id} />
      ))
    } 

      </div>
    </main>
  );
};

export default Home
