import React, { useEffect } from "react";
import { useGlobal } from './Components/utils/global.context';
import "./themeSwitch.css"
import moonIcon from "../Components/utils/moon.png";
import sunIcon from "../Components/utils/sun.png";

const ThemeSwitch = () => {
  const { theme, themeDispatch } = useGlobal();

  const toggleTheme = () => {
    themeDispatch({ type: "TOGGLE_THEME" });
  };

  useEffect(() => {
    const htmlElement = document.documentElement;
    htmlElement.classList.remove("light", "dark");
    htmlElement.classList.add(theme.darkMode ? "dark" : "light");
  }, [theme.darkMode]);


  return (
    <img
      //className="icon-theme"
      onClick={toggleTheme}
      src={theme.darkMode ? moonIcon : sunIcon}
      alt="Theme"
    />
  );
};

export default ThemeSwitch;
