import axios from "axios";
import { createContext, useState, useReducer, useEffect, useContext } from 'react';
import { reducer } from "../reducers/reducer.js "; 

const UserStates = createContext()

export const initialState = {
  list: [],
  favs: [],
  theme: "light", 
  //data: []
};

export const ContextGlobal = createContext(undefined);   //PARA QUE LA USABA???

export const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo
  //const [state, setState] = useState(initialState);
  const [state, dispatch] = useReducer(reducer, initialState)
  console.log(state)  //DESPUES SACARLO
  const url ='https://jsonplaceholder.typicode.com/users'

  useEffect(() => {
    axios(url)
    .then(res => dispatch({type: 'GET_USERS', payload: res.data.results}))
}, [])

//ESTO DE ABAJO NO ANDA VER SI LO SACO O LO ARREGLO
  const toggleTheme = () => {
    setState((prevState) => ({
      ...prevState,
      theme: prevState.theme === 'light' ? 'dark' : 'light',
    }));
  };
//HASTA ACA


  return (
    //<ContextGlobal.Provider value={{state, setState, toggleTheme}}>
    <UserStates.Provider value={{state, dispatch}}>
      {children}
    </UserStates.Provider>
   // </ContextGlobal.Provider>
  )
}

export default ContextProvider

export const useUserStates = () => useContext(UserStates)
