import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Footer from './Components/Footer';
import Navbar from './Components/Navbar';
import Home from './Routes/Home';
import Favs from './Routes/Favs';
import Detail from './Routes/Detail';
import Contact from './Routes/Contact';
import { ContextProvider } from './Components/utils/global.context';
import { useUserStates } from './Components/utils/global.context';

function App() {
  const { state } = useUserStates();

  // Verificar si state está definido antes de desestructurarlo
  const theme = state ? state.theme : 'light';

  return (
    <ContextProvider>
      <Router>
        <div>
          <Navbar />

          <Routes>
            <Route path="/" element={<Home theme={theme} />} />
            <Route path="/home" element={<Home theme={theme} />} />
            <Route path="/favs" element={<Favs theme={theme} />} />
            <Route path="/detail/:id" element={<Detail theme={theme} />} />
            <Route path="/contacts" element={<Contact theme={theme} />} />
          </Routes>

          <Footer />
        </div>
      </Router>
    </ContextProvider>
  );
}

export default App;
